package contract

import (
	"crypto/ecdsa"
	"github.com/avalido/mpc-controller/logger"
	crypto2 "github.com/avalido/mpc-controller/utils/crypto"
	"github.com/avalido/mpc-controller/utils/network"
	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/crypto"
	ethClient "github.com/ethereum/go-ethereum/ethclient"
	"github.com/pkg/errors"
	"github.com/stretchr/testify/suite"
	"os"
	"testing"
)

type CoordinatorTestSuite struct {
	suite.Suite
	chainID      int64
	privateKey   *ecdsa.PrivateKey
	contractAddr *common.Address
	client       *ethClient.Client
	bytecode     string
}

func (suite *CoordinatorTestSuite) SetupTest() {
	logger.DevMode = true

	suite.chainID = 43112
	suite.client = network.DefaultEthClient()
	suite.bytecode = ``

	privateKey, err := crypto.HexToECDSA("56289e99c94b6912bfc12adc093c9b51124f0dc54ac7a766b2bc5ccf558d8027")
	if err != nil {
		logger.Error("failed to parse secp256k1 private key", logger.Field{"error", errors.WithStack(err)})
		os.Exit(1)
	}
	suite.privateKey = privateKey

	addr, _, err := Deploy(suite.chainID, suite.client, suite.privateKey, suite.bytecode)
	if err != nil {
		logger.Error("failed to deploy coordinator contract", logger.Field{"error", errors.WithStack(err)})
		os.Exit(1)
	}
	suite.contractAddr = addr
}

func (suite *CoordinatorTestSuite) TestNew() {
	require := suite.Require()

	coordinator, err := NewCoordinator(suite.chainID, suite.contractAddr, suite.client)
	require.Truef(err == nil && coordinator != nil, "failed to create coordinator, ERROR: %v", err)
}

func (suite *CoordinatorTestSuite) TestCreateGroup() {
	require := suite.Require()

	var participant_priv_keys = []string{
		"59d1c6956f08477262c9e827239457584299cf583027a27c1d472087e8c35f21",
		"6c326909bee727d5fc434e2c75a3e0126df2ec4f49ad02cdd6209cf19f91da33",
		"5431ed99fbcc291f2ed8906d7d46fdf45afbb1b95da65fecd4707d16a6b3301b",
	}

	pubKeys, _ := crypto2.ExtractPubKeysForParticipants(participant_priv_keys)

	coordinator, err := NewCoordinator(suite.chainID, suite.contractAddr, suite.client)
	groupID, err := coordinator.CreateGroup_(suite.privateKey, pubKeys, 1)
	require.Nilf(err, "Failed to create group, error: %+v", errors.WithStack(err))
	require.NotEmpty(groupID)
}

func (suite *CoordinatorTestSuite) TestRequestKeygen() {
	require := suite.Require()

	coordinator, err := NewCoordinator(suite.chainID, suite.contractAddr, suite.client)

	// todo: get this value exactly from CreateGroup call.
	groupId := common.FromHex("3726383e52fd4cb603498459e8a4a15d148566a51b3f5bfbbf3cac7b61647d04")

	err = coordinator.RequestKeygen_(suite.privateKey, groupId)
	require.Nilf(err, "Failed to request keygen, error: %+v", errors.WithStack(err))
}

func TestCoordinatorTestSuite(t *testing.T) {
	suite.Run(t, new(CoordinatorTestSuite))
}
